query_json.query:
SELECT ?item ?itemLabel ?shortDescription ?image WHERE {
  ?item wdt:P31/wdt:P279* wd:Q16917 .
  ?item wdt:P17 wd:Q27 .
  SERVICE wikibase:label { bd:serviceParam wikibase:language "en". }
  OPTIONAL { ?item schema:description ?shortDescription . FILTER(LANG(?shortDescription)='en') }
  OPTIONAL { ?item wdt:P18 ?image }
}
ORDER BY ?itemLabel
LIMIT 15
