query_json.query:
SELECT ?item ?itemLabel ?shortDescription WHERE {
  ?item wdt:P31/wdt:P279* wd:Q43229 .  # Hospitals; change QID to target other classes
  SERVICE wikibase:label { bd:serviceParam wikibase:language 'en'. }
  OPTIONAL { ?item schema:description ?shortDescription . FILTER(LANG(?shortDescription)='en') }
} LIMIT 250
